INSERT INTO "public"."talks" ("id", "title", "description", "time_slot", "room", "track", "day", "metadata", "created_at", "updated_at", "speakers", "url_site", "cover_img") VALUES ('2f7dc336-67d4-48c6-824d-97ae6e7b6825', 'Orquestando soluciones AI con agentes multi-modales', 'Miércoles 24, 16:20 - 17:20 @ S\_ B En este taller profundizaremos en cada uno de los conceptos fundamentales de Machine Learning que conforman una solución de IA multimodal, combinando teoría y práctica con múltiples ejemplos de código y en CLI (línea de comandos.) Utilizando un framework de orquestación AI que nos permitirá acceder a millones de modelos abiertos y privados en una misma interfaz, aprenderemos los principios que son la base fundacional de soluciones AI empresariales: \\* Tokenización y modelos de embedding para administrar memorias \\* Modelos generativos NLP, de visión, y de audio \\* Construyendo agentes que integran múltiples modelos \\* RAG, Tool calling, MCP y A2A para extender las capacidades en tiempo real de nuestros agentes \\* Observability y flujos Human-in-the-Loop para alinear el output de los agentes AI Al finalizar la sesión, la audiencia contará con todas las piezas necesarias en la caja de herramientas para la orquestación de agentes inteligentes y distribuidos.', '16:20 - 17:20', 'S\_ B', 'DATA SCIENCE / AI', 'Miércoles 24', '{}', '2025-09-08T21:28:28.663885', '2025-09-08T21:28:28.663888', '{"Mariano Iglesias"}', 'https://nerdear.la/agenda/orquestando-soluciones-ai-con-agentes-multi-modales/', '');
